import { all, takeEvery, put, call } from "redux-saga/effects";
import actions from "./actions";
import {fetchCountryData} from '../../services/api'


export function* ADD_USER_COUNTRY({ payload }) {
debugger
  // const userData =  yield call(fetchCountryData, payload);
  const {country} = payload 
debugger
  yield put({
    type: "user/SET_STATE",
    payload: {
      countryName: country
    }
  });
}

export default function* rootSaga() {
  yield all([
    takeEvery(actions.ADD_USER_COUNTRY, ADD_USER_COUNTRY)
  ]);
}
