/**
 * This Component is used for Show Combine array
 *
 */
import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { fetchCountryData } from "../../services/api";
import axios from "axios";

const mapStateToProps = ({ dispatch, user }) => ({
  dispatch: dispatch,
  countryName: user.countryName,
});

const UserFormDetails = ({ dispatch, countryName, user }) => {
  const [userData, setUserData] = useState([]);

  useEffect(() => {
    debugger;
    console.log(user);
    axios
      .get(`https://restcountries.eu/rest/v2/name/` + "india")
      .then((res) => {
        debugger;
        setUserData(res.data);
      });
    debugger;
  }, []);

  return (
    <div>
      <p>Details Page</p>
      {userData.map((country, index) => {
          return (
            <div className="card" >
            <img className="card-img-top" src={country.flag} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">Card title</h5>
              <p className="card-text">
                Some quick example text to build on the card title and make up the
                bulk of the card's content.
              </p>
              <a href="#" className="btn btn-primary">
                Go somewhere
              </a>
            </div>
          </div>
          )
      })}

    </div>
  );
};

export default connect(mapStateToProps)(UserFormDetails);
