/**
 * This Component is used for Show Combine array
 *
 */
import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";

// import Utils from "../constants/utils";
import { connect } from "react-redux";
import { Button } from "react-bootstrap";

const mapStateToProps = ({ dispatch }) => ({
  dispatch: dispatch,
});

const UserForm = ({ dispatch }) => {
const history = useHistory();

  const [countryName, setCountryName] = useState("");
  const [isDisable, setIsDisable] = useState(true);
  const [isSubmitted, setIsSubmitted] = useState(false);


  useEffect(() => {}, []);

  const onSubmit = () => {
    setIsSubmitted(true)
    dispatch({
        type: 'user/ADD_USER_COUNTRY',
        payload: countryName,
      })
      history.push("/country-details");


  };

  const validateForm = () => {
    if(isSubmitted && countryName === ''){
        return true
    } else{
        return false
    }
  }

  const onHandleChange = (event) => {
    setCountryName(event.target.value)
    setIsSubmitted(false)
    if(event.target.value !== ''){
        setIsDisable(false)
    } else{
        setIsDisable(true)

    }

  }

  return (
    <div>
      <h3>User Details</h3>
      <div className="container"></div>
      <form>
        <div classNames="form-group">
          <label for="country">Country</label>
          <input
            type="country"
            classNames="form-control"
            id="country"
            placeholder=" Enter country"
            required
            value={countryName}
            onChange={ (e) => onHandleChange(e)}
          />
        </div>

        <button type="submit" classNames="btn btn-primary" disabled={isDisable} onClick={onSubmit}>
          Submit
        </button>
      </form>
    </div>
  );
};

export default connect(mapStateToProps)(UserForm);
