/**
 * This Component is used for Show Combine array
 *
 */
import React, { useEffect } from "react";
import Utils from "../constants/utils";
import * as Constants from "../constants/constant";
import { connect } from "react-redux";
import { Button } from "react-bootstrap";

const mapStateToProps = ({ dispatch }) => ({
  dispatch: dispatch
});

const CombineArray = ({ dispatch }) => {
  const temp_array1 = Constants.TEMP_ARRAY_1;
  const temp_array2 = Constants.TEMP_ARRAY_2;
  const temp_array3 = Constants.TEMP_ARRAY_3;

  useEffect(() => {}, []);

  /**
   * This method is used for getting combined array from three array
   */
  const getCombineArray = () => {
    let result = Utils.CombineArray(3, temp_array1, temp_array2, temp_array3);
    dispatch({
      type: "user/ADD_COMBINE_ARR",
      payload: result
    });
  };

  /**
   * This method is used for clear output result
   */
  const onClear = () => {
    dispatch({
      type: "user/REMOVE_ARR",
      payload: "combineArr"
    });
  };

  return (
    <div>
      <p>Please Click on submit button for getting combine array value: </p>
      <Button variant="outline-secondary" onClick={getCombineArray}>Submit</Button>
      <Button  variant="outline-secondary" onClick={onClear}>Clear</Button>
    </div>
  );
};

export default connect(mapStateToProps)(CombineArray);
